import React, { useState } from "react";
import logo from '../asset/img/Alogo.png';
import "../asset/style/Log_in.css";
import { useForm } from "react-hook-form";
import Swal from 'sweetalert2'


function Sign_up(){
    const [data,setData] = useState({
        username: '',
        email: '',
        password: '',
    })

    const url = 'https://localhost:3001/'

    function Enviar(e){
        e.preventDefault();
        axios.post(url,{
            username: data.username,
            email: data.email,
            password: data.password
        })
        .then(res=>{
            console.log(res.data)
        })
    }

    function handle(e){
        const newData = {...data}
        newData[e.target.id] = e.target.value
        setData(newData)
        console.log(newData)
    }

    const { handleSubmit, register, formState: { errors } } = useForm();

    const onSubmit = values =>{
        console.log(values);
        Swal.fire(
            'Bienvenido!',
            'Cuenta Creada con Exito',
            'success'
        )
    }


    return(
        <div className="container w-75 mt-5 bg-light rounder shadow">
            <div className="row align-items-stretch">
                <div className="col bg d-none d-lg-block col-md-5 col-lg-7 col-xl-6 rounder">

                </div>


                <div className="col p-5 rounder-end">
                    <div className="text-end">
                        <img src={logo} width="150"></img>
                    </div>
                    <h2 className="fw-bold text-center py-5">Registrate</h2>

                    <form onSubmit={handleSubmit(onSubmit)}>
                    <div className="mb-4">
                            <label for="text" className="form-label">Escribe Tu Nombre</label>
                            <input type="text" className="form-control" onchange={(e)=>handle(e)} id="username" value={data.username} placeholder="username" {...register("username",{
                                required: {
                                    value: true,
                                    message: "El campo requerido",
                                }
                            })}></input>
                            {errors.username && <span className="text-danger">{errors.username.message}</span>}
                        </div>
                        <div className="mb-4">
                            <label for="email" className="form-label">Escribe Tu Correo</label>
                            <input type="text" className="form-control" onchange={(e)=>handle(e)} id="email" value={data.email} placeholder="Email"{...register("email",{
                                required: {
                                    value: true,
                                    message: "El campo requerido",
                                },
                                pattern:{
                                    value: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i,
                                    message: "Invalido email"
                                }
                            })}></input>
                            {errors.email && <span className="text-danger">{errors.email.message}</span>}
                        </div>
                        <div className="mb-4">
                            <label for="password" className="form-label">Escribe Tu Constraseña</label>
                            <input type="password" className="form-control" onchange={(e)=>handle(e)} id="password" value={data.password} placeholder="Password"{...register("password",{
                                required: {
                                    value: true,
                                    message: "El campo requerido",
                                },
                                minLength:{
                                    value: 8,
                                    message: "La contraseña debe tener minimo 8 caracteres"
                                }
                            })}></input>
                            {errors.password && <span className="text-danger">{errors.password.message}</span>}
                        </div>
                        <div className="d-grid">
                            <button type="submit"  className="btn btn-primary">Registrar</button>
                        </div>

                        <div className="my-3">
                            <span className="fw-bold">Tienes Cuenta? <a href="/">Login</a></span>
                        </div>
                    </form>
                </div>
            </div>
        </div>
    );
}

export default Sign_up;
