import React from "react";
import { useForm } from "react-hook-form";
import logo from '../asset/img/Alogo.png';
import "../asset/style/Log_in.css";


function Login(){

    const { handleSubmit, register, formState: { errors } } = useForm();
    const onSubmit = values => console.log(values);

    const submit = values => {
        alert(values);
    }




    return(
        <div className="container w-75 mt-5 bg-light rounder shadow">
            <div className="row align-items-stretch">
                <div className="col bg d-none d-lg-block col-md-5 col-lg-7 col-xl-6 rounder">

                </div>


                <div className="col p-5 rounder-end">
                    <div className="text-end">
                        <img src={logo} width="150"></img>
                    </div>
                    <h2 className="fw-bold text-center py-5">Bienvenido</h2>

                    <form onSubmit={handleSubmit(onSubmit)}>
                        <div className="mb-4">
                            <label for="email" className="form-label">Escribe Tu Correo</label>
                            <input type="text" className="form-control" id="email" placeholder="Email" {...register("email",{
                                required: {
                                    value: true,
                                    message: "El campo requerido",
                                },
                                pattern:{
                                    value: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i,
                                    message: "Invalido email"
                                }
                            })}></input>
                            {errors.email && <span className="text-danger">{errors.email.message}</span>}
                        </div>
                        <div className="mb-4">
                            <label for="password" className="form-label">Escribe Tu ConstraseÃ±a</label>
                            <input type="password" className="form-control" placeholder="Password" {...register("password",{
                                required: {
                                    value: true,
                                    message: "El campo requerido",
                                },
                                minLength:{
                                    value: 8,
                                    message: "La contraseÃ±a debe tener minimo 8 caracteres"
                                }
                            })}></input>
                            {errors.password && <span className="text-danger">{errors.password.message}</span>}
                        </div>
                        <div className="my-3">
                        <span><a href="/Recover_pass">Forgot password?</a></span>
                        </div>
                        <div className="d-grid">
                            <button type="submit"  className="btn btn-primary">Iniciar Sesion</button>
                        </div>

                        <div className="my-3">
                            <span className="fw-bold">No tienes Cuenta? <a href="/Sign_up">Create account</a></span>
                        </div>
                    </form>
                </div>
            </div>
        </div>
    );
}

export default Login;
