import React, { useEffect, useState } from "react";
import axios from 'axios';
import Swal from "sweetalert2";


import { Button, Card, CardBody, CardTitle, CardSubtitle, CardText, CardGroup, CardImg, CardColumns, CardHeader } from 'reactstrap';


function Upimg() {

    const [data, setData] = useState([0])

    useEffect(() => {
        axios.get('http://localhost:3000/api/product/view')
            .then(res => {
                console.log(res.data)
                setData(res.data)
            })
            .catch(err => {
                console.log(err)
            })
    }, [])


    const postDelete = (id, e) => {
        e.preventDefault();
        axios.delete(`http://localhost:3000/api/img/eliminar?id=${id}`)
            .then(res => console.log(res))
            .catch(err => console.log(err))
        Swal.fire({
            title: 'Eliminando...',
            text: 'Usuario de Id: ' + id + ' Eliminado',
            icon: 'warning',
            confirmButtonText: 'Ok'
        })
    }

    return (
        <CardColumns className="py-5">
            {
                data.map((data) => (
                    <CardColumns>
                    <Card className="me-4">
                        <CardImg alt="Card image cap" src="https://picsum.photos/318/180" top width="100%" />
                        <CardBody>
                            <CardTitle tag="h5">{data.title}</CardTitle><br />
                            <CardSubtitle className="mb-2" tag="h6"><span className=" text-primary">Precio: </span>{data.price} USD</CardSubtitle><br />
                            <CardSubtitle className="mb-2" tag="h6"><span className=" text-primary">Stock: </span>{data.amount}</CardSubtitle><br />
                            <CardText className="mb-2" tag="h6"><span className="text-primary">Descripcion:  </span>{data.description}</CardText><br />
                            <Button>Comprar</Button>
                        </CardBody>
                    </Card>
                    </CardColumns>
                ))
            }
        </CardColumns>

    )

}

export default Upimg;
