import React from "react";
import { Link } from "react-router-dom";
import logo from '../asset/img/Alogo.png';
import "../asset/style/Recover_pass.css";
import { useForm } from "react-hook-form";
import Swal from 'sweetalert2'

function Reset_pass() {

    const { handleSubmit, register, formState: { errors } } = useForm();

    const onSubmit = values =>{
        console.log(values);
        Swal.fire(
            'Password Actualizado!',
            '',
            'success'
        )
    }

    return(
        <div className="container w-75 mt-5 bg-light rounder shadow">
        <div className="row align-items-stretch">
            <div className="col bg1 d-none d-lg-block col-md-5 col-lg-10 col-xl-6 rounder">

            </div>


            <div className="col p-5 rounder-end">
                <div className="text-end">
                    <img src={logo} width="150"></img>
                </div>
                <h2 className="fw-bold text-center py-5">Restablecer Password</h2>
                <form onSubmit={handleSubmit(onSubmit)}>
                    <div className="mb-4">
                        <label for="password" className="form-label">Escribe Tu Nueva Constraseña</label>
                        <input type="password" className="form-control" placeholder="Password" {...register("password1",{
                                required: {
                                    value: true,
                                    message: "El campo requerido",
                                },
                                minLength:{
                                    value: 8,
                                    message: "La contraseña debe tener minimo 8 caracteres"
                                }
                            })}></input>
                            {errors.password1 && <span className="text-danger">{errors.password1.message}</span>}
                    </div>
                    <div className="mb-4">
                        <label for="password" className="form-label">Confirma Tu Constraseña</label>
                        <input type="password" className="form-control" placeholder="Password" {...register("password2",{
                                required: {
                                    value: true,
                                    message: "El campo requerido",
                                },
                                minLength:{
                                    value: 8,
                                    message: "La contraseña debe tener minimo 8 caracteres",
                                },
                                pattern:{
                                    value: password1 == password2,
                                }
                            })}></input>
                            {errors.password2 && <span className="text-danger">{errors.password2.message}</span>}
                            <h1>{errors.password.messag}</h1>
                    </div>
                    <div className="d-grid">
                        <button type="submit" className="btn btn-primary">Restablecer</button>
                    </div>
                </form>
            </div>
        </div>
    </div>
    );
}

export default Reset_pass;
